mapper.select:
SELECT id,name,level FROM categories WHERE level = #{level}
AND EXISTS ( SELECT 1 FROM category_tree_paths WHERE categories.id = category_tree_paths.descendant_id AND ancestor_id = #{ancestorId} )
ORDER BY name

SELECT NOT EXISTS ( SELECT 1 FROM categories WHERE level = #{level} AND name ILIKE #{name}
AND EXISTS ( SELECT 1 FROM category_tree_paths WHERE categories.id = category_tree_paths.descendant_id AND ancestor_id = #{ancestorId} )
) AS no_duplicate;
